<?php /*FoolishGames.com Redirect ScriptVersion 1.0Written by Lucas Holt5/9/02Purpose: Redirects client to new location based on user input.Implementation: Uses legacy http get vars array to retrive user input.  first php version.*/    // Declare Variables.     $url = Null;    $redirect = True;        // Get User Input    $url = trim( $HTTP_GET_VARS['url'] );    $urlcomp = parse_url( $url );    if ( $urlcomp['scheme'] != "http" ) {        $redirect = False;    }    if ( ( $url != "" ) && ( is_string($url) ) ) {        // Send Header        header("Location: $url");    } else {        echo ("An error has occured.");    }?>